import { useState } from 'react';
import styled from 'styled-components';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { addProduct } from './redux/productSlice';
import { useDispatch } from 'react-redux';

function Items({ id, image, amount, title, items }) {
	const [quantity, setQuantity] = useState(0);
	const [product, setProduct] = useState({});
	const dispatch = useDispatch();

	const handleQuantity = (type) => {
		if (type === 'dec') {
			quantity > 1 && setQuantity(quantity - 1);
		} else {
			setQuantity(quantity + 1);
		}
	};

	const handleClick = () => {
		dispatch(addProduct({ ...product, quantity }));
	};

	return (
		<StyledItems>
			<div className='items_image'>
				<img src={image} alt='bri' />
			</div>
			<div className='items_content'>
				<div className='items_content_title'>
					<p>{title}</p>
				</div>

				<div className='items_content_cover'>
					<div className='items_content_cover_left'>
						<h4>${amount}</h4>
					</div>

					<div className='items_content_cover_right'>
						<div
							style={{ pointerEvents: quantity === 1 ? 'none' : '' }}
							className='removeicon'
							onClick={() => handleQuantity('dec')}>
							<RemoveIcon />
						</div>
						<div className='quantitynum'>{quantity}</div>
						<div className='addicon' onClick={() => handleQuantity('inc')}>
							<AddIcon />
						</div>
						<Button onClick={handleClick}>ADD TO CART</Button>
					</div>
				</div>
			</div>
		</StyledItems>
	);
}
const StyledItems = styled.div`
	display: flex;
	box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
	background-color: white;
	outline: none;
	border: none;
	border-radius: 0.8rem;
	padding: 1rem;
	margin-bottom: 1rem;

	@media (max-width: 850px) {
		flex-direction: column;
	}

	.items_image {
		display: flex;
		justify-content: center;
		align-items: center;
	}

	.items_content {
		display: flex;
		flex-direction: column;
		justify-content: space-between;
		width: 100%;
		height: 23rem;
		padding-left: 1rem;
		.items_content_title {
			font-size: 2rem;
			font-family: 'Inconsolata', monospace;
			p {
				margin-bottom: 1rem;
			}
			.truncate {
				font-style: italic;
			}
		}

		.items_content_cover {
			display: flex;
			justify-content: space-between;
			align-items: center;
			width: 100%;
			.items_content_cover_left {
				h4 {
					font-size: 2rem;
					font-weight: bold;
					color: #000;
				}
			}
			.items_content_cover_right {
				display: flex;
				justify-content: space-between;
				align-items: center;

				.removeicon {
					border-radius: 0.5rem;
					padding: 0.5rem;
					background-color: #d3d8da;
					cursor: pointer;
				}
				.removeshoppingicon {
					margin: 1.5rem;
					border-radius: 0.5rem;
					padding: 0.5rem;
					background-color: red;
					color: white;
					cursor: pointer;
				}
				.addicon {
					border-radius: 0.5rem;
					padding: 0.5rem;
					background-color: #d3d8da;
					cursor: pointer;
				}
				.quantitynum {
					margin: 1rem;
					font-size: 1.3rem;
					font-weight: bold;
				}
			}
		}
	}
`;

export default Items;
